class FixedNavigation{constructor(t){const e=document.querySelector(t);window.addEventListener("scroll",this.fixedNav.bind(e))}fixedNav(){window.pageYOffset>0?(this.style.position="fixed",this.style.top="0px",this.style.left="0px",this.style.background="rgba(0,0,0, 0.85)",this.parentElement.style.zIndex="999"):(this.style.position="absolute",this.style.background="rgba(0,0,0, 0.2)",this.parentElement.style.zIndex="5")}}