class Validation{constructor(){}validateForm(e){const a=e.target;let t;switch(a.dataset.rule){case"name":t=/^[А-Яа-яЁёA-Za-z]{3,11}$/.test(a.value);break;case"email":t=/^[a-zA-Z._-]+\d*@[a-z]+?\.[a-z]{2,3}$/.test(a.value);break;case"phone":t=this.validatePhone(a.value);break;case"message":t=!0;break;default:t=!1}t?a.style.border="2px solid green":(a.style.border="2px solid red",a.value="")}validatePhone(e){let a=[67,96,97,98,50,66,95,99,63,73,93,91,92,94].map(function(e){return"0"+e}).join("|");return!!new RegExp("/\\+38\\("+a+"\\)\\-(d{3})\\-(d{2})\\-(d{2})/").test(e)}}